function windowLoaded(){768<=window.innerWidth&&document.getElementById("navigation-list").setAttribute("aria-hidden","false");var e=document.getElementById("sitewide-header"),t=e.getElementsByTagName("a");e.addEventListener("click",toggleControlled);for(var i=t.length-1;0<=i;i--)t[i].addEventListener("click",closeMenu)}function windowResized(){var e=document.getElementById("navigation-list");768<=window.innerWidth?e.setAttribute("aria-hidden","false"):e.setAttribute("aria-hidden","true")}function toggleControlled(e){if(e.target&&"BUTTON"==e.target.nodeName){var t=e.target,i=t.getAttribute("aria-controls"),n=(t.getAttribute("aria-expanded"),document.getElementById(i));"true"===n.getAttribute("aria-hidden")?(n.setAttribute("aria-hidden","false"),t.setAttribute("aria-expanded","true")):(n.setAttribute("aria-hidden","true"),t.setAttribute("aria-expanded","false"))}}function closeMenu(){if(window.innerWidth<=767){var e=document.getElementById("navigation-list"),t=document.getElementById("toggle-navigation"),i=e.getAttribute("aria-hidden");t.getAttribute("aria-expanded");"false"===i&&(e.setAttribute("aria-hidden","true"),t.setAttribute("aria-expanded","false"))}}window.onload=windowLoaded,window.onresize=windowResized;